INSERT INTO `actions` (`id`, `userid`, `name`, `type`, `subtype`, `source`, `object`) VALUES
(5, 0, 'Psychic Drain', 'legendary', NULL, 'monsters', '{\"tag\": \"legendary2\", \"cost\": \"2\", \"name\": \"Psychic Drain\", \"type\": \"legendary\", \"values\": [{\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"6\"}, \"name\": \"value1\", \"type\": \"psychic damage\", \"expression\": \"\"}], \"ability\": \"STR\", \"levelMin\": \"17\", \"priority\": \"10\", \"description\": \"One creature charmed by [name] takes {value1}, and the aboleth regains hit points equal to the damage the creature takes.\"}'),
(6, 0, 'Enslave', 'action', NULL, 'monsters', '{\"tag\": \"action2\", \"name\": \"Enslave\", \"type\": \"action\", \"values\": [{\"name\": \"value2\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+WIS\"}], \"charges\": \"LVL\\\\7\", \"levelMin\": \"10\", \"priority\": \"1\", \"recharge\": \"day\", \"description\": \"[Name] targets one creature it can see within 30 feet of it. The target must succeed on a {value2} saving throw or be magically charmed by [name] until [name] dies or until it is on a different plane of existence from the target. The charmed target is under [name]\'s control and can\'t take reactions, and [name] and the target can communicate telepathically with each other over any distance.\\n\\nWhenever the charmed target takes damage, the target can repeat the saving throw. On a success, the effect ends. No more than once every 24 hours, the target can also repeat the saving throw when it is at least 1 mile away from [name].\\n\"}'),
(7, 0, 'Mucous Cloud', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Mucous Cloud\", \"type\": \"trait\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}], \"priority\": \"12\", \"description\": \"While underwater, [name] is surrounded by transformative mucus. A creature that touches [name] or that hits [them] with a melee attack while within 5 feet of it must make a  {value1} saving throw. On a failure, the creature is diseased for 1d4 hours. The diseased creature can breathe only underwater.\"}'),
(8, 0, 'Probing Telepathy', 'trait', NULL, 'monsters', '{\"tag\": \"trait2\", \"name\": \"Probing Telepathy\", \"type\": \"trait\", \"levelMin\": \"10\", \"priority\": \"14\", \"description\": \"If a creature communicates telepathically with [name],  [name] learns the creature\'s greatest desires if [name] can see the creature.\"}'),
(10, 0, 'Tentacle +disease', 'attack', NULL, 'monsters', '{\"tag\": \"tentacle\", \"name\": \"Tentacle\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"12\", \"levelMax\": \"\", \"levelMin\": \"\", \"diceNumber\": \"1\", \"diceIncrement\": \"\", \"levelInterval\": \"\"}, \"name\": \"value2\", \"type\": \"acid damage\", \"expression\": \"\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"6\", \"reach\": \"5+((SIZE-1)/2)*5\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"1+((SIZE-1)/2)*1\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"1\", \"description\": \"{attack1}. If the target is a creature, it must succeed on a {value1} or become diseased. The disease has no effect for 1 minute and can be removed by any magic that cures disease. After 1 minute, the diseased creature\'s skin becomes translucent and slimy, the creature can\'t regain hit points unless it is underwater, and the disease can be removed only by heal or another disease-curing spell of 6th level or higher. When the creature is outside a body of water, it takes {value2} every 10 minutes unless moisture is applied to the skin before 10 minutes have passed.\"}'),
(15, 0, 'Change Shape', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Change Shape\", \"type\": \"action\", \"levelMin\": \"11\", \"priority\": \"12\", \"description\": \"[Name] magically polymorphs into a humanoid or beast that has a challenge rating equal to or less than [their] own, or back into [their] true form. [They] reverts to [their] true form if [they] dies. Any equipment [they] is wearing or carrying is absorbed or borne by the new form ([name]\'s choice). In a new form, [name] retains [their] game statistics and ability to speak, but [their] AC, movement modes, Strength, Dexterity, and special senses are replaced by those of the new form, and [they] gains any statistics and capabilities (except class features, legendary actions, and lair actions) that the new form has but that [they] lacks.\"}'),
(18, 0, 'Divine Awareness', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Divine Awareness\", \"type\": \"trait\", \"levelMin\": \"10\", \"priority\": \"10\", \"description\": \"[Name] knows if it hears a lie.\"}'),
(22, 0, 'Healing Touch', 'action', NULL, 'monsters', '{\"tag\": \"action2\", \"name\": \"Healing Touch\", \"type\": \"action\", \"values\": [{\"dice\": {\"die\": \"8\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"3\"}, \"name\": \"value1\", \"type\": \"hit point\", \"expression\": \"LVL/5\"}], \"charges\": \"LVL/4\", \"levelMin\": \"4\", \"priority\": \"10\", \"recharge\": \"day\", \"description\": \"[Name] touches another creature. The target magically regains {value1} hit points and is freed from any curse, disease, poison, blindness, or deafness.\\n\"}'),
(23, 0, 'Angelic Weapons', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Angelic Weapons\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"8\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"2\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value1\", \"type\": \"radiant damage\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"[Name]\'s weapon attacks are magical. When [name] hits with any weapon, the weapon deals an extra {value1} (included in the attack).\"}'),
(27, 0, 'Flying Sword', 'action', NULL, 'monsters', '{\"tag\": \"action4\", \"name\": \"Flying Sword\", \"type\": \"action\", \"levelMin\": \"5\", \"priority\": \"10\", \"description\": \"[Name] releases [their] greatsword to hover magically in an unoccupied space within 5 feet of [them]. If [name] can see the sword, [name] can mentally command it as a bonus action to fly up to 50 feet and either make one attack against a target or return to [name]\'s hands. If the hovering sword is targeted by any effect, [name] is considered to be holding it. The hovering sword falls if [name] dies.\"}'),
(29, 0, 'Teleport', 'legendary', NULL, 'monsters', '{\"tag\": \"legendary0\", \"name\": \"Teleport\", \"type\": \"legendary\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"((24*LVL)/18)*5\"}], \"ability\": \"STR\", \"levelMin\": \"17\", \"priority\": \"10\", \"description\": \"[Name] magically teleports, along with any equipment [they] is wearing or carrying, up to {value1} to an unoccupied space [they] can see\"}'),
(30, 0, 'Searing Burst', 'legendary', NULL, 'monsters', '{\"tag\": \"legendary1\", \"cost\": \"2\", \"name\": \"Searing Burst\", \"type\": \"legendary\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Dexterity\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"4\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value2\", \"type\": \"fire damage\", \"expression\": \"\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"4\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value3\", \"type\": \"radiant damage\", \"expression\": \"\"}], \"ability\": \"STR\", \"levelMin\": \"17\", \"priority\": \"12\", \"description\": \"[Name] emits magical, divine energy. Each creature of [their] choice in a 10-foot radius must make a {value1} saving throw, taking {value2} plus {value3} on a failed save, or half as much damage on a successful one.\"}'),
(31, 0, 'Blinding Gaze', 'legendary', NULL, 'monsters', '{\"tag\": \"legendary2\", \"cost\": \"3\", \"name\": \"Blinding Gaze\", \"type\": \"legendary\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF\"}], \"ability\": \"STR\", \"levelMin\": \"17\", \"priority\": \"14\", \"description\": \"[Name] targets one creature [they] can see within 30 feet of [them]. If the target can see [them], the target must succeed on a {value1} saving throw or be blinded until magic such as the [spell:lesser restoration] spell removes the blindness.\"}'),
(34, 0, 'Slaying Longbow', 'attack', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Slaying Longbow\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"hit point\", \"expression\": \"(100*LVL)/18\"}, {\"name\": \"value2\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"8\", \"range\": \"150\", \"reach\": \"\", \"targets\": \"1\", \"rangeMax\": \"600\", \"damageType\": \"piercing damage\", \"diceNumber\": \"2\", \"properties\": [\"weapon\", \"ranged\"]}, \"enchantment\": {\"dice\": {\"die\": \"8\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"2\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"type\": \"radiant damage\"}}], \"levelMin\": \"17\", \"priority\": \"10\", \"description\": \"{attack1}. If the target is a creature that has {value1} or fewer, it must succeed on a  {value2} saving throw or die.\\n\"}'),
(35, 0, 'Antimagic Susceptibility', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Antimagic Susceptibility\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] is incapacitated while in the area of an [spell:antimagic field]. If targeted by [spell:dispel magic], [name] must succeed on a Constitution saving throw against the caster\'s spell save DC or fall unconscious for 1 minute.\"}'),
(44, 0, 'Damage Transfer', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Damage Transfer\", \"type\": \"trait\", \"priority\": \"12\", \"description\": \"While [they] is grappling a creature, [name] takes only half the damage dealt to [them], and the creature grappled by [name] takes the other half.\"}'),
(46, 0, 'Smother', 'attack', NULL, 'monsters', '{\"tag\": \"action0\", \"name\": \"Smother\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"+\", \"expression\": \"PROF+STR\"}, {\"name\": \"value2\", \"type\": \"\", \"expression\": \"8+PROF+STR\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value3\", \"type\": \"bludgeoning damage\", \"expression\": \"LVL\\\\2\"}], \"ability\": \"STR\", \"priority\": \"10\", \"description\": \"_Melee Weapon Attack:_ {value1} to hit, reach 5ft, one [sizesmaller] or smaller creature. _Hit:_ The creature is grappled (escape DC {value2}). Until this grapple ends, the target is restrained, blinded, and at risk of suffocating, and the rug can\'t smother another target. In addition, at the start of each of the target\'s turns, the target takes {value3}.\"}'),
(47, 0, 'Acid Spray', 'action', NULL, 'monsters', '{\"tag\": \"action1\", \"name\": \"Acid Spray\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"(1+SIZE/2)*10\"}, {\"name\": \"value2\", \"type\": \"DC Dexterity\", \"expression\": \"8+PROF+STR\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"2\"}, \"name\": \"value3\", \"type\": \"acid damage\", \"expression\": \"\"}], \"charges\": \"1\", \"priority\": \"10\", \"recharge\": \"6–6\", \"description\": \"[Name] spits acid in a line that is {value1} long and 5 feet wide, provided that [they] has no creature grappled. Each creature in that line must make a {value2} saving throw, taking {value3} on a failed save, or half as much damage on a successful one.\"}'),
(50, 0, 'Heated Body', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Heated Body\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"\", \"diceNumber\": \"1\", \"diceIncrement\": \"\", \"levelInterval\": \"\"}, \"name\": \"value1\", \"type\": \"fire damage\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"A creature that touches [name] or hits [them] with a melee attack while within 5 feet of [them] takes {value1}.\"}'),
(54, 0, 'Petrifying Gaze', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Petrifying Gaze\", \"type\": \"trait\", \"values\": [{\"name\": \"value2\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}], \"priority\": \"10\", \"description\": \"If a creature starts its turn within 30 feet of [name] and the two of them can see each other, [name] can force the creature to make a {value2} saving throw if [name] isn\'t incapacitated. On a failed save, the creature magically begins to turn to stone and is restrained. It must repeat the saving throw at the end of its next turn. On a success, the effect ends. On a failure, the creature is petrified until freed by the [spell:greater restoration] spell or other magic.\\n\\nA creature that isn\'t surprised can avert its eyes to avoid the saving throw at the start of its turn. If it does so, it can\'t see [name] until the start of its next turn, when it can avert its eyes again.If it looks at [name] in the meantime, it must immediately make the save. \\n\\nIf the basilisk sees [their] reflection within 30 feet of [them] in bright light, [they] mistakes [them]self for a rival and targets [them]self with [their] gaze.\"}'),
(59, 0, 'Constrict', 'action', NULL, 'monsters', '{\"tag\": \"action2\", \"name\": \"Constrict\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"+\", \"expression\": \"PROF+STR\"}, {\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"10\"}, \"name\": \"value2\", \"type\": \"bludgeoning damage\", \"expression\": \"STR\"}, {\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"10\"}, \"name\": \"value3\", \"type\": \"slashing damage\", \"expression\": \"STR\"}, {\"name\": \"value4\", \"type\": \"\", \"expression\": \"8+PROF+STR-2\"}], \"priority\": \"10\", \"description\": \"_Melee Weapon Attack:_ {value1} to hit, reach 5ft, one [sizesmaller] or smaller creature. _Hit:_ {value2} plus {value3}. The target is grappled (escape DC {value4}) if [name] isn\'t already constricting a creature, and the target is restrained until this grapple ends.\"}'),
(62, 0, 'Brute', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Brute\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"A melee weapon deals one extra die of its damage when [name] hits with it (included in the attack).\"}'),
(63, 0, 'Surprise Attack', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Surprise Attack\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"3\"}, \"name\": \"value2\", \"type\": \"\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"If [name] surprises a creature and hits it with an attack during the first round of combat, the target takes an extra {value2} damage from the attack.\"}'),
(67, 0, 'Standing Leap', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Standing Leap\", \"type\": \"trait\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"10+((4*LVL)/9)*5\"}, {\"name\": \"value2\", \"type\": \"feet\", \"expression\": \"5+((2*LVL)/9)*5\"}], \"priority\": \"10\", \"description\": \"[Name]\'s long jump is up to {value1} and its high jump is up to {value2}, with or without a running start.\"}'),
(68, 0, 'Deadly Leap', 'action', NULL, 'monsters', '{\"tag\": \"action1\", \"name\": \"Deadly Leap\", \"type\": \"action\", \"values\": [{\"name\": \"value2\", \"type\": \"feet\", \"expression\": \"5 + ((2*LVL)/9)*5\"}, {\"name\": \"value3\", \"type\": \"DC Strength\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value4\", \"type\": \"bludgeoning damage\", \"expression\": \"STR\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value5\", \"type\": \"slashing damage\", \"expression\": \"STR\"}], \"levelMin\": \"6\", \"priority\": \"10\", \"description\": \"If [name] jumps at least {value2} as part of [their] movement, [they] can then use this action to land on [their] feet in a space that contains one or more other creatures. Each of those creatures must succeed on a {value3} or Dexterity saving throw (target\'s choice) or be knocked prone and take {value4} plus {value5}. On a successful save, the creature takes only half the damage, isn\'t knocked prone, and is pushed 5 feet out of [name]\'s space into an unoccupied space of the creature\'s choice. If no unoccupied space is within range, the creature instead falls prone in [name]\'s space.\"}'),
(71, 0, 'Charge', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Charge\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"2\"}, \"name\": \"value2\", \"type\": \"\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"If [name] moves at least 30 feet straight toward a target and then hits it with a [meleeweapon] attack on the same turn, the target takes an extra {value2}.\"}'),
(82, 0, 'Sense Magic', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Sense Magic\", \"type\": \"trait\", \"priority\": \"12\", \"description\": \"[Name] senses magic within 120 feet of [them] at will. This trait otherwise works like the [spell:detect magic] spell but isn\'t itself magical.\"}'),
(89, 0, 'Pushing Kick', 'reaction', NULL, 'monsters', '{\"tag\": \"reaction0\", \"name\": \"Pushing Kick\", \"type\": \"reaction\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"((LVL+1)\\\\5)*5\"}], \"levelMin\": \"14\", \"priority\": \"10\", \"description\": \"When a creature within [name]\'s reach attacks [them] with a melee or ranged attack, [they] can make one attack with [their] [kick] against the creature. If the attack hits, the creature is also pushed up to {value1} in the opposite direction in addition to taking damage from the attack.\"}'),
(90, 0, 'Stunning Kick', 'legendary', NULL, 'monsters', '{\"tag\": \"legendary2\", \"cost\": \"2\", \"name\": \"Stunning Kick\", \"type\": \"legendary\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+STR\"}], \"ability\": \"STR\", \"levelMin\": \"18\", \"priority\": \"10\", \"description\": \"[Name] makes one [kick] attack against a creature within reach. If the attack hits, the creature must succeed on a {value1} saving throw or be stunned until the end of [name]\'s next turn.\"}'),
(91, 0, 'Death Kick', 'attack', NULL, 'monsters', '{\"tag\": \"death kick\", \"name\": \"Death Kick\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"4\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"1\"}, \"name\": \"value2\", \"type\": \"bludgeoning damage\", \"expression\": \"STR\"}], \"ability\": \"STR\", \"levelMin\": \"25\", \"priority\": \"10\", \"description\": \"[Name] makes one [kick] attack against a creature within reach. If the attack hits, the creature must succeed on a {value1} saving throw. If it fails, the creature is reduced to 0 hit points. If it succeeds, the creature takes {value2}.\"}'),
(94, 0, 'Kick +prone', 'attack', NULL, 'monsters', '{\"tag\": \"kick0\", \"name\": \"Kick\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+STR\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"4\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"2+LVL/6\", \"properties\": [\"weapon\", \"melee\"]}}], \"levelMin\": \"9\", \"priority\": \"10\", \"description\": \"{attack1}. If the target is a [size] or smaller creature, it must succeed on a {value1} saving throw or be knocked prone.\\n\"}'),
(95, 0, 'Damage Transfer', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Damage Transfer\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"While attached to a creature, [name] takes only half the damage dealt to [them] (rounded down), and that creature takes the other half.\"}'),
(97, 0, 'Light Sensitivity', 'trait', NULL, 'monsters', '{\"tag\": \"trait2\", \"name\": \"Light Sensitivity\", \"type\": \"trait\", \"priority\": \"14\", \"description\": \"While in bright light,[name] has disadvantage on attack rolls and Wisdom (Perception) checks that rely on sight.\"}'),
(100, 0, 'Moan', 'action', NULL, 'monsters', '{\"tag\": \"action2\", \"name\": \"Moan\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+CHA\"}], \"levelMin\": \"8\", \"priority\": \"10\", \"description\": \"Each creature within 60 feet of [name] that can hear [their] moan and that isn\'t an aberration must succeed on a {value1} saving throw or become frightened until the end of [name]\'s next turn. If a creature\'s saving throw is successful, the creature is immune to [name]\'s moan for the next 24 hours.\"}'),
(101, 0, 'Phantasms', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Phantasms\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"(LVL-3)/3\"}], \"charges\": \"1\", \"levelMin\": \"6\", \"priority\": \"12\", \"recharge\": \"short\", \"description\": \"[Name] magically creates {value1} illusory duplicates of [them]self if [they] isn\'t in bright light. The duplicates move with [them] and mimic [their] actions, shifting position so as to make it impossible to track which cloaker is the real one. If [name] is ever in an area of bright light, the duplicates disappear. \\n\\nWhenever any creature targets [name] with an attack or a harmful spell while a duplicate remains, that creature rolls randomly to determine whether it targets [name] or one of the duplicates. A creature is unaffected by this magical effect if it can\'t see or if it relies on senses other than sight. \\n\\nA duplicate has [name]\'s AC and uses [their] saving throws. If an attack hits a duplicate, or if a duplicate fails a saving throw against an effect that deals damage, the duplicate disappears.\\n\"}'),
(105, 0, 'Magic Weapons', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Magic Weapons\", \"type\": \"trait\", \"priority\": 10, \"description\": \"[Name]\'s weapon attacks are magical.\"}'),
(106, 0, 'Shielded Mind', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Shielded Mind\", \"type\": \"trait\", \"priority\": \"12\", \"description\": \"[Name] is immune to scrying and to any effect that would sense [their] emotions, read [their] thoughts, or detect [their] location.\"}'),
(109, 0, 'Constrict', 'attack', NULL, 'monsters', '{\"tag\": \"attack3\", \"name\": \"Constrict\", \"type\": \"attack\", \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"6\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"LVL\\\\7\", \"properties\": [\"weapon\", \"melee\"]}}], \"levelMin\": \"5\", \"priority\": \"12\", \"description\": \"{attack1}, and the target is grappled (escape DC 15). Until this grapple ends, the target is restrained, and [name] can\'t constrict another target.\"}'),
(112, 0, 'Echolocation', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Echolocation\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] can\'t use [their] blindsight while deafened.\"}'),
(113, 0, 'Crush', 'attack', NULL, 'monsters', '{\"tag\": \"attack2\", \"name\": \"Crush\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Strength\", \"expression\": \"8+PROF+STR\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"SIZE*3\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"LVL\\\\5\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"10\", \"description\": \"{attack1}, and [name] attaches to the target. If the target is [sizegreater] or smaller and [name] has advantage on the attack roll, [they] attaches by engulfing the target\'s head, and the target is also blinded and unable to breathe while [name] is attached in this way.\\n\\nWhile attached to the target, [name] can attack no other creature except the target but has advantage on [their] attack rolls. [Name]\'s speed also becomes 0, [they] can\'t benefit from any bonus to [their] speed, and [they] moves with the target.\\n\\nA creature can detach [name] by making a successful {value1} check as an action. On its turn, [name] can detach [them]self from the target by using 5 feet of movement.\"}'),
(114, 0, 'Darkness Aura', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Darkness Aura\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"-feet\", \"expression\": \"5+SIZE*5\"}], \"charges\": \"1\", \"priority\": \"12\", \"recharge\": \"day\", \"description\": \"A {value1} radius of magical darkness extends out from [name], moves with [them], and spreads around corners. The darkness lasts as long as [name] maintains concentration, up to 10 minutes (as if concentrating on a spell). Darkvision can\'t penetrate this darkness, and no natural light can illuminate it. If any of the darkness overlaps with an area of light created by a spell of 2nd level or lower, the spell creating the light is dispelled.\"}'),
(115, 0, 'Death Throes', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Death Throes\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"2\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"1\"}, \"name\": \"value1\", \"type\": \"fire damage\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"When [name] dies, [they] explodes, and each creature within 30 feet of [them] must make a DC 20 Dexterity saving throw, taking {value1} on a failed save, or half as much damage on a successful one. The explosion ignites flammable objects in that area that aren\'t being worn or carried, and it destroys [name]\'s weapons.\"}'),
(116, 0, 'Fire Aura', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Fire Aura\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"10\"}, \"name\": \"value1\", \"type\": \"fire damage\", \"expression\": \"\"}], \"levelMin\": \"6\", \"priority\": \"12\", \"description\": \"At the start of each of [name]\'s turns, each creature within 5 feet of [them] takes {value1}, and flammable objects in the aura that aren\'t being worn or carried ignite. A creature that touches [name] or hits [them] with a melee attack while within 5 feet of [them] takes {value1}.\"}'),
(125, 0, 'Fetid Cloud', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Fetid Cloud\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}, {\"name\": \"value2\", \"type\": \"\", \"expression\": \"5 + (LVL\\\\8)*5\"}], \"charges\": \"LVL\\\\6\", \"priority\": \"16\", \"recharge\": \"day\", \"description\": \"A {value2}-foot radius of disgusting green gas extends out from [name]. The gas spreads around corners, and its area is lightly obscured. It lasts for 1 minute or until a strong wind disperses it. Any creature that starts its turn in that area must succeed on a {value1} saving throw or be poisoned until the start of its next turn. While poisoned in this way, the target can take either an action or a bonus action on its turn, not both, and can\'t take reactions.\"}'),
(129, 0, 'Pincer', 'attack', NULL, 'monsters', '{\"tag\": \"attack2\", \"name\": \"Pincer\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Strength\", \"expression\": \"10+STR\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"6\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"LVL\\\\10\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"12\", \"description\": \"{attack1}. If the target is a [sizesmaller] or smaller creature, it is grappled (escape DC {value1}). [Name] has two pincers, each of which can grapple only one target.\"}'),
(138, 0, 'Tail +grapple', 'attack', NULL, 'monsters', '{\"tag\": \"attack5\", \"name\": \"Tail\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"10 + PROF + STR\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"10\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"bludgeoning damage\", \"diceNumber\": \"1 + SIZE/4\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"14\", \"description\": \"{attack1}. If the target is [sizesmaller] or smaller, it is grappled (escape DC {value1}). Until this grapple ends, the target is restrained, [name] can automatically hit the target with [their] tail, and [name] can\'t make tail attacks against other targets.\"}'),
(141, 0, 'Reactive', 'trait', NULL, 'monsters', '{\"tag\": \"trait2\", \"name\": \"Reactive\", \"type\": \"trait\", \"priority\": \"14\", \"description\": \"[Name] can take one reaction on every turn in a combat.\"}'),
(148, 0, 'Horror Nimbus', 'action', NULL, 'monsters', '{\"tag\": \"Horror Nimbus\", \"name\": \"Horror Nimbus\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+CHA\"}], \"charges\": \"1\", \"priority\": \"16\", \"recharge\": \"5–6\", \"description\": \"[Name] magically emits scintillating, multicolored light. Each creature within 15 feet of [name] that can see the light must succeed on a {value1} saving throw or be frightened for 1 minute. A creature can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. If a creature\'s saving throw is successful or the effect ends for it, the creature is immune to [name]\'s Horror Nimbus for the next 24 hours.\"}'),
(149, 0, 'Teleport', 'action', NULL, 'monsters', '{\"tag\": \"action5\", \"name\": \"Teleport\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"30 + 10*(9*LVL)/16\"}], \"levelMin\": \"10\", \"priority\": \"18\", \"description\": \"[Name] magically teleports, along with any equipment [they] is wearing or carrying, up to {value1} to an unoccupied space it can see.\"}'),
(151, 0, 'Shapechanger', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Shapechanger\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] can use [their] action to polymorph into a beast form that resembles a bat (speed 10 ft fly 40 ft), a centipede (40 ft, climb 40 ft), or a toad (40 ft, swim 40 ft), or back into [their] true form. [Their] statistics are the same in each form, except for the speed changes noted. Any equipment [they] is wearing or carrying isn\'t transformed. [They] reverts to [their] true form if [they] dies.\"}'),
(153, 0, 'Scare', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Scare\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+CHA\"}], \"charges\": \"LVL\\\\5\", \"priority\": \"12\", \"recharge\": \"day\", \"description\": \"One creature of [name]\'s choice within 20 feet of it must succeed on a {value1} saving throw or be frightened for 1 minute. The target can repeat the saving throw at the end of each of its turns, with disadvantage if [name] is within line of sight, ending the effect on itself on a success.\"}'),
(162, 0, 'Spores', 'action', NULL, 'monsters', '{\"tag\": \"action4\", \"name\": \"Spores\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"14\"}, \"name\": \"value2\", \"type\": \"poison damage\", \"expression\": \"\"}], \"charges\": \"1\", \"levelMin\": \"6\", \"priority\": \"16\", \"recharge\": \"6–6\", \"description\": \"A 15­-foot­-radius cloud of toxic spores extends out from [name]. The spores spread around corners. Each creature in that area must succeed on a {value1} saving throw or become poisoned. While poisoned in this way, a target takes {value2} at the start of each of its turns. A target can repeat the saving throw at the end of each of its turns, ending the effect on itself on a success. Emptying a vial of  holy water on the target also ends the effect on it.\"}'),
(163, 0, 'Stunning Screech', 'action', NULL, 'monsters', '{\"tag\": \"action5\", \"name\": \"Stunning Screech\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CON\"}], \"charges\": \"1\", \"levelMin\": \"10\", \"priority\": \"18\", \"recharge\": \"day\", \"description\": \"[Name] emits a horrific screech. Each creature within 20 feet of [them] that can hear it and that isn\'t a demon must succeed on a {value1} saving throw or be stunned until the end of [name]\'s next turn.\"}'),
(164, 0, 'Devil\'s Sight', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Devil\'s Sight\", \"type\": \"trait\", \"priority\": 12, \"description\": \"Magical darkness doesn\'t impede [name]\'s darkvision.\"}'),
(167, 0, 'Barbed Hide', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Barbed Hide\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"14\"}, \"name\": \"value1\", \"type\": \"piercing damage\", \"expression\": \"\"}], \"priority\": \"10\", \"description\": \"At the start of each of [their] turns, [name] deals {value1} to any creature grappling [them].\"}'),
(172, 0, 'Steadfast', 'trait', NULL, 'monsters', '{\"tag\": \"trait2\", \"name\": \"Steadfast\", \"type\": \"trait\", \"priority\": \"14\", \"description\": \"[Name] can\'t be frightened while [they] can see an allied creature within 30 feet of [them].\"}'),
(175, 0, 'Weapon +infernal wound', 'attack', NULL, 'monsters', '{\"tag\": \"weapon\", \"name\": \"Glaive\", \"type\": \"attack\", \"values\": [{\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"13\"}, \"name\": \"value1\", \"type\": \"\", \"expression\": \"\"}, {\"name\": \"value2\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+CON\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"10\", \"reach\": \"\", \"targets\": \"1\", \"damageType\": \"slashing damage\", \"diceNumber\": \"1\", \"properties\": [\"weapon\", \"melee\", \"reach\"]}}], \"priority\": \"14\", \"description\": \"{attack1}. If the target is a creature other than an undead or a construct, it must succeed on a DC 12 Constitution saving throw or lose {value1} hit points at the start of each of its turns due to an infernal wound. Each time [name] hits the wounded target with this attack, the damage dealt by the wound increases by {value1}. Any creature can take an action to stanch the wound with a successful {value2} (Medicine) check. The wound also closes if the target receives magical healing.\"}'),
(185, 0, 'Chain', 'attack', NULL, 'monsters', '{\"tag\": \"attack3\", \"name\": \"Chain\", \"type\": \"attack\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"5\"}, \"name\": \"value2\", \"type\": \"piercing damage\", \"expression\": \"\"}], \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"6\", \"reach\": \"5*(LVL\\\\5)\", \"targets\": \"1\", \"damageType\": \"slashing damage\", \"diceNumber\": \"LVL\\\\5\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"12\", \"description\": \"{attack1}. The target is grappled (escape DC {value1}) if [name] isn\'t already grappling a creature. Until this grapple ends, the target is restrained and takes {value2} at the start of each of its turns.\"}'),
(186, 0, 'Animate Chains', 'action', NULL, 'monsters', '{\"tag\": \"action4\", \"name\": \"Animate Chains\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"LVL\\\\3\"}, {\"name\": \"value2\", \"type\": \"\", \"expression\": \"14 + (6*LVL)/10 \"}], \"charges\": \"LVL\\\\15\", \"levelMin\": \"5\", \"priority\": \"14\", \"recharge\": \"short\", \"description\": \"Up to {value1} chains [name] can see within 60 feet of [them] magically sprout razor-edged barbs and animate under [name]\'s control, provided that the chains aren\'t being worn or carried.\\n\\nEach animated chain is an object with AC {value2}, 20 hit points, resistance to piercing damage, and immunity to psychic and thunder damage. When [name] uses multiattack on [their] turn, it can use each animated chain to make one additional chain attack. An animated chain can grapple one creature of its own but can\'t make attacks while grappling. An animated chain reverts to its inanimate state if reduced to 0 hit points or if [name] is incapacitated or dies.\"}'),
(187, 0, 'Unnerving Mask', 'reaction', NULL, 'monsters', '{\"tag\": \"reaction5\", \"name\": \"Unnerving Mask\", \"type\": \"reaction\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Wisdom\", \"expression\": \"9+PROF+CHA\"}], \"priority\": \"10\", \"description\": \"When a creature [name] can see starts its turn within 30 feet of [name], [name] can create the illusion that [they] looks like one of the creature\'s departed loved ones or bitter enemies. If the creature can see [name], it must succeed on a {value1} saving throw or be frightened until the end of its turn.\"}'),
(190, 0, 'Parry', 'reaction', NULL, 'monsters', '{\"tag\": \"reaction5\", \"name\": \"Parry\", \"type\": \"reaction\", \"priority\": \"10\", \"description\": \"[Name] adds 4 to [their] AC against one melee attack that would hit [them]. To do so, [name] must see the attacker and be wielding a melee weapon.\"}'),
(193, 0, 'Hellish Weapons', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Hellish Weapons\", \"type\": \"trait\", \"values\": [{\"dice\": {\"die\": \"8\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"6\"}, \"name\": \"value1\", \"type\": \"poison damage\", \"expression\": \"\"}], \"levelMin\": \"7\", \"priority\": \"10\", \"description\": \"[Name]\'s weapon attacks are magical and deal an extra {value1} on a hit (included in the attacks).\"}'),
(198, 0, 'Melee1 and Melee2, or Spell instead', 'multiattack', NULL, 'monsters', '{\"tag\": \"multiattack2\", \"name\": \"Multiattack\", \"type\": \"multiattack\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"2 + LVL>9 + LVL>18 + LVL>30 + LVL>44\"}, {\"name\": \"value2\", \"type\": \"\", \"expression\": \"1 + LVL>9 + LVL>18 + LVL>30 + LVL>44\"}], \"priority\": \"10\", \"description\": \"[Name] makes {value1} melee attacks: {value2} with [their] fork and one with [their] tail. [They] can use Hurl Flame in place of any melee attack.\"}'),
(199, 0, 'Hurl Flame', 'attack', NULL, 'monsters', '{\"tag\": \"attack5\", \"name\": \"Hurl Flame\", \"type\": \"attack\", \"ability\": \"CHA\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"6\", \"range\": \"150\", \"reach\": \"\", \"targets\": \"1\", \"rangeMax\": \"150\", \"damageType\": \"fire damage\", \"diceNumber\": \"LVL\\\\5\", \"properties\": [\"spell\", \"ranged\"]}}], \"priority\": \"16\", \"description\": \"{attack1}. If the target is a flammable object that isn\'t being worn or carried, it also catches fire.\"}'),
(209, 0, 'Wall of Ice', 'action', NULL, 'monsters', '{\"tag\": \"action6\", \"name\": \"Wall of Ice\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Dexterity\", \"expression\": \"8+PROF+CHA\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"2\"}, \"name\": \"value2\", \"type\": \"cold damage\", \"expression\": \"\"}, {\"name\": \"value3\", \"type\": \"hit point\", \"expression\": \"(30*LVL)/19\"}, {\"name\": \"value4\", \"type\": \"DC Constitution\", \"expression\": \"8+PROF+CHA\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"1\", \"diceIncrement\": \"1\", \"levelInterval\": \"4\"}, \"name\": \"value5\", \"type\": \"cold damage\", \"expression\": \"\"}], \"charges\": \"1\", \"priority\": \"18\", \"recharge\": \"6–6\", \"description\": \"[Name] magically forms an opaque wall of ice on a solid surface [they] can see within 60 feet of [them]. The wall is 1 foot thick and up to 30 feet long and 10 feet high, or it\'s a hemispherical dome up to 20 feet in diameter.\\n\\nWhen the wall appears, each creature in its space is pushed out of it by the shortest route. The creature chooses which side of the wall to end up on, unless the creature is incapacitated. The creature then makes a {value1}  saving throw, taking {value2} on a failed save, or half as much damage on a successful one.\\n\\nThe wall lasts for 1 minute or until [name] is incapacitated or dies. The wall can be damaged and breached; each 10-­foot section has AC 5, {value3}, vulnerability to fire damage, and immunity to acid, cold, necrotic, poison, and psychic damage. If a section is destroyed, it leaves behind a sheet of frigid air in the space the wall occupied. Whenever a creature finishes moving through the frigid air on a turn, willingly or otherwise, the creature must make a {value4} saving throw, taking {value5} on a failed save, or half as much damage on a successful one. The frigid air dissipates when the rest of the wall vanishes.\"}'),
(214, 0, 'Shapechanger', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Shapechanger\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] can use [their] action to polymorph into a beast form that resembles a rat (speed 20 ft.), a raven (20 ft., fly 60 ft.), or a spider (20 ft., climb 20 ft.), or back into [their] true form. [Their] statistics are the same in each form, except for the speed changes noted. Any equipment [they] is wearing or carrying isn\'t transformed. [They] reverts to [their] true form if [they] dies.\"}'),
(218, 0, 'Invisibility', 'bonus', NULL, 'monsters', '{\"tag\": \"action5\", \"name\": \"Invisibility\", \"type\": \"bonus\", \"levelMin\": \"10\", \"priority\": \"12\", \"description\": \"[Name] magically turns invisible until [they] attacks or until [their] concentration ends (as if concentrating on a spell). Any equipment [name] wears or carries is invisible with [them].\"}'),
(221, 0, 'Hellish Rejuvenation', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Hellish Rejuvenation\", \"type\": \"trait\", \"priority\": \"12\", \"description\": \"A lemure that dies in the Nine Hells comes back to life with all its hit points in 1d10 days unless it is killed by a good-aligned creature with a bless spell cast on that creature or its remains are sprinkled with holy water.\"}'),
(231, 0, 'Fear Aura', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Fear Aura\", \"type\": \"trait\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Wisdom\", \"expression\": \"8+PROF+CHA\"}], \"levelMin\": \"19\", \"priority\": \"10\", \"description\": \"Any creature hostile to [name] that starts its turn within 20 feet of [name] must make a {value1} saving throw, unless [name] is incapacitated. On a failed save, the creature is frightened until the start of its next turn. If a creature\'s saving throw is successful, the creature is immune to [name]\'s Fear Aura for the next 24 hours.\"}'),
(240, 0, 'Trampling Charge', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Trampling Charge\", \"type\": \"trait\", \"values\": [{\"name\": \"value1\", \"type\": \"DC Strength\", \"expression\": \"7+STR\"}], \"priority\": \"10\", \"description\": \"If [name] moves at least 20 feet straight toward a creature and then hits it with a gore attack on the same turn, that target must succeed on a {value1} saving throw or be knocked prone. If the target is prone, [name] can make one stomp attack against it as a bonus action.\"}'),
(241, 0, 'Gore', 'attack', NULL, 'monsters', '{\"tag\": \"attack1\", \"name\": \"Gore\", \"type\": \"attack\", \"ability\": \"STR\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"8\", \"reach\": \"5\", \"targets\": \"1\", \"damageType\": \"piercing damage\", \"diceNumber\": \"LVL\\\\3\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"10\", \"description\": \"{attack1}\"}'),
(248, 0, 'Shapechanger', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Shapechanger\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] can use [their] action to polymorph into a [sizesmaller] or [size] humanoid [they] has seen, or back into [their] true form. [Their] statistics, other than [their] size, are the same in each form. Any equipment [they] is wearing or carrying isn\'t transformed. [They] reverts to [their] true form if [they] dies.\"}'),
(249, 0, 'Ambusher', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Ambusher\", \"type\": \"trait\", \"priority\": \"12\", \"description\": \"[Name] has advantage on attack rolls against any creature [they] has surprised.\"}'),
(253, 0, 'Read Thoughts', 'action', NULL, 'monsters', '{\"tag\": \"action5\", \"name\": \"Read Thoughts\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"20 + 5*(8*LVL)/8\"}], \"priority\": \"14\", \"description\": \"[Name] magically reads the surface thoughts of one creature within {value1} of it. The effect can penetrate barriers, but 3 feet of wood or dirt, 2 feet of stone, 2 inches of metal, or a thin sheet of lead blocks it. While the target is in range, [name] can continue reading its thoughts, as long as [name]\'s concentration isn\'t broken (as if concentrating on a spell). While reading the target\'s mind, [name] has advantage on Wisdom (Insight) and Charisma (Deception, Intimidation, and Persuasion) checks against the target.\"}'),
(261, 0, 'Legendary Resistance', 'trait', NULL, 'monsters', '{\"tag\": \"trait1\", \"name\": \"Legendary Resistance\", \"type\": \"trait\", \"charges\": \"3\", \"levelMin\": \"15\", \"priority\": \"12\", \"recharge\": \"day\", \"description\": \"If [name] fails a saving throw, [they] can choose to succeed instead.\"}'),
(319, 0, 'Lightning Breath', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Lightning Breath\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"20 + 10*(4*LVL)/16\"}, {\"name\": \"value2\", \"type\": \"DC Dexterity\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"10\", \"levelMax\": \"\", \"levelMin\": \"1\", \"diceNumber\": \"3\", \"diceIncrement\": \"1\", \"levelInterval\": \"2\"}, \"name\": \"value3\", \"type\": \"lightning damage\", \"expression\": \"\"}], \"charges\": \"1\", \"priority\": \"16\", \"recharge\": \"5–6\", \"description\": \"[Name] exhales lightning in an {value1}-foot line that is 5 feet wide. Each creature in that line must make a {value2} saving throw, taking {value3} on a failed save, or half as much damage on a successful one.\"}'),
(384, 0, 'Fire Breath', 'action', NULL, 'monsters', '{\"tag\": \"action3\", \"name\": \"Fire Breath\", \"type\": \"action\", \"values\": [{\"name\": \"value1\", \"type\": \"\", \"expression\": \"10 + 10*(2*LVL)/17\"}, {\"name\": \"value2\", \"type\": \"DC Dexterity\", \"expression\": \"8+PROF+CON\"}, {\"dice\": {\"die\": \"6\", \"levelMax\": \"\", \"levelMin\": \"3\", \"diceNumber\": \"2\", \"diceIncrement\": \"1\", \"levelInterval\": \"1\"}, \"name\": \"value3\", \"type\": \"fire damage\", \"expression\": \"\"}], \"charges\": \"1\", \"priority\": \"16\", \"recharge\": \"5–6\", \"description\": \"[Name] exhales fire in a {value1}-foot cone. Each creature in that area must make a {value2} saving throw, taking {value3} on a failed save, or half as much damage on a successful one.\"}');
INSERT INTO `actions` (`id`, `userid`, `name`, `type`, `subtype`, `source`, `object`) VALUES
(392, 0, 'Ice Walk', 'trait', NULL, 'monsters', '{\"tag\": \"trait0\", \"name\": \"Ice Walk\", \"type\": \"trait\", \"priority\": \"10\", \"description\": \"[Name] can move across and climb icy surfaces without needing to make an ability check. Additionally, difficult terrain composed of ice or snow doesn\'t cost [them] extra movement.\"}'),
(431, 0, 'Change Shape', 'action', NULL, 'monsters', '{\"tag\": \"action9\", \"name\": \"Change Shape\", \"type\": \"action\", \"levelMin\": \"5\", \"priority\": \"26\", \"description\": \"[Name] magically polymorphs into a humanoid or beast that has a challenge rating no higher than [their] own, or back into [their] true form. [They] reverts to [their] true form if [they] dies. Any equipment [they] is wearing or carrying is absorbed or borne by the new form ([name]\'s choice).\\nIn a new form, [name] retains [their] alignment, hit points, Hit Dice, ability to speak, proficiencies, Legendary Resistance, lair actions, and Intelligence, Wisdom, and Charisma scores, as well as this action. [Their] statistics and capabilities are otherwise replaced by those of the new form, except any class features or legendary actions of that form.\"}'),
