(655, 0, 'Shriek', 'reaction', NULL, 'monsters', '{\"tag\": \"reaction1\", \"name\": \"Shriek\", \"type\": \"reaction\", \"values\": [{\"name\": \"value1\", \"type\": \"feet\", \"expression\": \"SIZE*10\"}, {\"name\": \"value2\", \"type\": \"feet\", \"expression\": \"SIZE*100\"}], \"priority\": \"10\", \"description\": \"When bright light or a creature is within {value1} of [name], [they] emits a shriek audible within {value2} of [them]. [Name] continues to shriek until the disturbance moves out of range and for 1d4 of [name]\'s turns afterward.\"}'),
(658, 0, 'Rotting Touch', 'attack', NULL, 'monsters', '{\"tag\": \"attack2\", \"name\": \"Rotting Touch\", \"type\": \"attack\", \"ability\": \"CON\", \"attacks\": [{\"name\": \"attack1\", \"attributes\": {\"die\": \"2 + SIZE*2\", \"reach\": \"5 + (SIZE>2)*(SIZE-2)*5\", \"targets\": \"1\", \"damageType\": \"necrotic damage\", \"diceNumber\": \"LVL\\\\5\", \"properties\": [\"weapon\", \"melee\"]}}], \"priority\": \"12\", \"description\": \"{attack1}\"}'),
