(1548, 0, 'Uncanny Dodge', 'reaction', NULL, 'classes', '{\"tag\": \"reaction0\", \"name\": \"Uncanny Dodge\", \"type\": \"reaction\", \"levelMin\": \"5\", \"priority\": \"20\", \"description\": \"When an attacker that [name] can see hits [them] with an attack, [they] can use [their] reaction to halve the attack\'s damage against [them].\"}'),
(1551, 0, 'Dirty Tricks', 'trait', NULL, 'classes', '{\"tag\": \"trait2\", \"name\": \"Dirty Tricks\", \"type\": \"trait\", \"levelMin\": \"4\", \"priority\": \"20\", \"description\": \"[name] has mastered techniques to take advantage of every drop in any enemy\'s guard, gaining the following benefits:\\n- When [name] hits a creature with an opportunity attack, the creature\'s speed becomes 0 for the rest of the turn.\\n- Creatures provoke opportunity attacks from [name] even if they take the Disengage action before leaving [their] reach.\"}'),
(1554, 0, 'Supreme Sneak', 'trait', NULL, 'classes', '{\"tag\": \"trait0\", \"name\": \"Supreme Sneak\", \"type\": \"trait\", \"levelMin\": \"9\", \"priority\": \"20\", \"description\": \"[Name] has advantage on a Dexterity (Stealth) check if [they] moves no more than half [their] speed on the same turn.\"}'),
