INSERT INTO `damagetypes` (`userid`, `name`, `description`) VALUES
(0, 'acid', 'The corrosive spray of a black dragon\'s breath and the dissolving enzymes secreted by a black pudding deal acid damage.'),
(0, 'bludgeoning', 'Blunt force attacks. Hammers, falling, constriction, and the like deal bludgeoning damage.'),
(0, 'cold', 'The infernal chill radiating from an ice devil\'s spear and the frigid blast of a white dragon\'s breath deal cold damage.'),
(0, 'fire', 'Red dragons breathe fire, and many spells conjure flames to deal fire damage.'),
(0, 'force', 'Force is pure magical energy focused into a damaging form. Most effects that deal force damage are spells, including magic missile and spiritual weapon.'),
(0, 'lightning', 'A lightning bolt spell and a blue dragon\'s breath deal lightning damage.'),
(0, 'necrotic', 'Necrotic damage, dealt by certain undead and a spell such as chill touch, withers matter and even the soul.'),
(0, 'piercing', 'Puncturing and impaling attacks, including spears and monsters\' bites, deal piercing damage.'),
(0, 'poison', 'Venomous stings and the toxic gas of a green dragon\'s breath deal poison damage.'),
(0, 'psychic', 'Mental abilities such as a mind flayer\'s psionic blast deal psychic damage.'),
(0, 'radiant', 'Radiant damage, dealt by a cleric\'s flame strike spell or an angel\'s smiting weapon, sears the flesh like fire and overloads the spirit with power.'),
(0, 'slashing', 'Swords, axes, and monsters\' claws deal slashing damage.'),
(0, 'thunder', 'A concussive burst of sound, such as the effect of the thunderwave spell, deals thunder damage.');
